-- ----------------------------
-- Table structure for jl_category
-- ----------------------------
DROP TABLE IF EXISTS `jl_category`;
CREATE TABLE `jl_category` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT 'ID',
  `parent_id` int(11) NOT NULL DEFAULT '0' COMMENT '上级ID',
  `name` varchar(50) NOT NULL COMMENT '栏目名称',
  `en_name` varchar(50) NOT NULL COMMENT '英文名称',
  `link` varchar(255) DEFAULT NULL COMMENT '外部链接',
  `file_path` varchar(255) DEFAULT NULL COMMENT '栏目图片',
  `template` int(1) DEFAULT '0' COMMENT '模板-0列表模板1单页模板2图片模板3下载模板',
  `meta_title` varchar(255) DEFAULT NULL COMMENT 'SEO标题',
  `meta_keywords` varchar(255) DEFAULT NULL COMMENT 'seo关键字',
  `meta_description` varchar(255) DEFAULT NULL COMMENT 'seo描述',
  `display` int(1) NOT NULL DEFAULT '0' COMMENT '0显示1隐藏',
  `index_view` varchar(50) NOT NULL COMMENT '首页显示0不显示1导航2首页3页尾',
  `list_order` int(5) NOT NULL DEFAULT '0' COMMENT '排序',
  `level` int(1) NOT NULL DEFAULT '0' COMMENT '级别',
  `create_at` datetime NOT NULL COMMENT '创建时间',
  `update_at` datetime NOT NULL COMMENT '更新时间',
  PRIMARY KEY (`id`),
  UNIQUE KEY `name` (`name`) USING BTREE,
  UNIQUE KEY `en_name` (`en_name`) USING BTREE
) ENGINE=InnoDB AUTO_INCREMENT=15 DEFAULT CHARSET=utf8;

