  `content` text NOT NULL COMMENT '内容',
  `author` varchar(50) DEFAULT NULL COMMENT '作者',
  `browse_num` int(11) NOT NULL DEFAULT '0' COMMENT '浏览量',
  `meta_keywords` varchar(255) DEFAULT NULL COMMENT 'seo关键字',
