DROP TABLE IF EXISTS `jl_system_ad`;
CREATE TABLE `jl_system_ad` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '广告ID',
  `p_id` int(11) NOT NULL COMMENT '广告位置ID',
  `name` varchar(50) NOT NULL COMMENT '广告名称',
  `link` varchar(100) NOT NULL COMMENT '广告链接',
  `image_path` varchar(100) NOT NULL COMMENT '广告图片路径',
  `description` varchar(255) DEFAULT NULL COMMENT '广告描述',
  `display` int(1) NOT NULL DEFAULT '0' COMMENT '0显示1隐藏',
  `list_order` int(5) NOT NULL DEFAULT '0' COMMENT '排序',
  `create_at` datetime NOT NULL COMMENT '创建时间',
  `update_at` datetime NOT NULL COMMENT '更新时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=9 DEFAULT CHARSET=utf8 COMMENT='图片广告';

